import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';

function Home() {
  const navigate = useNavigate();
  const [isClickable, setIsClickable] = useState(true); // Track if navigation can happen

  // Handler function to navigate after animation
  const handleNavigation = (path) => {
    if (isClickable) {
      setIsClickable(false); // Disable further clicks
      // Add delay to allow the animation to complete
      setTimeout(() => {
        navigate(path);
        setIsClickable(true); // Re-enable after navigation
      }, 4000); // Adjust duration to match animation timing (400 ms delay)
    }
  };

  return (
    <section className="home">
      <h2 name="home">developing...</h2>
      <div className="nav-options">

        {/* Replace Link to Projects with an animated button */}
        <motion.div
          whileTap={{ scale: 0.9, rotate: 5 }}
          transition={{ duration: 0.3 }}
          style={{
            display: 'inline-block',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            borderRadius: '5px',
            textAlign: 'center',
            cursor: 'pointer',
            margin: '10px',
          }}
          onClick={() => handleNavigation('/projects')}
        >
          Projects
        </motion.div>

        {/* Replace Link to Courses with an animated button */}
        <motion.div
          whileTap={{ scale: 0.9, rotate: 5 }}
          transition={{ duration: 0.3 }}
          style={{
            display: 'inline-block',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            borderRadius: '5px',
            textAlign: 'center',
            cursor: 'pointer',
            margin: '10px',
          }}
          onClick={() => handleNavigation('/courses')}
        >
          Classes
        </motion.div>

        {/* Replace Link to About with an animated button */}
        <motion.div
          whileTap={{ scale: 0.9, rotate: 5 }}
          transition={{ duration: 0.3 }}
          style={{
            display: 'inline-block',
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            borderRadius: '5px',
            textAlign: 'center',
            cursor: 'pointer',
            margin: '10px',
          }}
          onClick={() => handleNavigation('/about')}
        >
          About Me
        </motion.div>

      </div>
    </section>
  );
}

export default Home;
