// AnimatedButton.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AnimatedButton() {
  const navigate = useNavigate();
  const [isClicked, setIsClicked] = useState(false);

  const handleClick = () => {
    // Trigger animation by setting the state to true
    setIsClicked(true);

    // Add a delay to navigate to allow the animation to play
    setTimeout(() => {
      navigate('/target-route'); // Replace '/target-route' with your desired route
      setIsClicked(false); // Reset the state after navigating
    }, 400); // Delay matches the animation duration
  };

  return (
    <div
      onClick={handleClick}
      style={{
        display: 'inline-block',
        padding: '10px 20px',
        backgroundColor: '#007bff',
        color: '#fff',
        borderRadius: '5px',
        textAlign: 'center',
        cursor: 'pointer',
        transition: 'transform 0.4s ease', // Smooth animation effect
        transform: isClicked ? 'scale(0.9)' : 'scale(1)', // Scale effect when clicked
      }}
    >
      Click Me!
    </div>
  );
}

export default AnimatedButton;
